sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var ga = new GlideAjax('GetFormDefaultsService');
	ga.addParam('sysparm_name', 'getOverriddenWorkRole');
	ga.addParam('sysparm_sys_id', newValue);
	ga.getXML(InitializeForm);

	function InitializeForm(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");

		var responseObject = JSON.parse(answer);
		g_form.setValue('name', responseObject.name);
		g_form.setValue('rate_code', responseObject.rate_code);
		g_form.setValue('rate_type', responseObject.rate_type);

		//don't update if record already had overrides
		if (!oldValue) {
			g_form.setValue('short_description', responseObject.short_description);
			g_form.setValue('hourly_rate', responseObject.hourly_rate);
			}
		}
	}
