sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	var ga = new GlideAjax('GetFormDefaultsService');
	ga.addParam('sysparm_name', 'getOverriddenWorkType');
	ga.addParam('sysparm_sys_id', newValue);
	ga.getXML(InitializeForm);

	function InitializeForm(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");

		var responseObject = JSON.parse(answer);
		g_form.setValue('name', responseObject.name);

		//don't update if record already had overrides
		if (!oldValue) {
			g_form.setValue('price_adjustment_type', responseObject.price_adjustment_type);
			g_form.setValue('price_adjustment', responseObject.price_adjustment);
			g_form.setValue('minimum_hours', responseObject.minimum_hours);
			g_form.setValue('maximum_hours', responseObject.maximum_hours);
			g_form.setValue('round_to_minutes', responseObject.round_to_minutes);
			g_form.setValue('accrual_type', responseObject.accrual_type);
			g_form.setValue('for_utilization', responseObject.for_utilization);
//			overriddenValues.default = r.default.toString();
			}
		}
	}
