sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	if (!current.override_defaults) {
		var mr = new GlideRecord('ast_service');
		mr.addQuery('account', current.customer);
		mr.addQuery('x_175353_contracts_is_master', true);
//restore this after demo		mr.addQuery('state', 'active');
//bombing?		mr.addQuery('starts', '<=', current.invoice_date.getDate().getDisplayValue());
		mr.query();

		if(mr.next()) {
			if (current.operation() == 'insert') {
				current.include_tax_fee = mr.x_175353_contracts_include_tax_fee;
				current.include_prorated_deprovisions = mr.x_175353_contracts_include_prorated_deprovisions;
				current.include_prorated_provisions = mr.x_175353_contracts_include_prorated_provisions;
			}
			else {
				if (
					!(current.include_tax_fee == mr.x_175353_contracts_include_tax_fee
					&& current.include_prorated_deprovisions ==  mr.x_175353_contracts_include_prorated_deprovisions
					&& current.include_prorated_provisions == mr.x_175353_contracts_include_prorated_provisions)
				) {
					current.include_tax_fee = mr.x_175353_contracts_include_tax_fee;
					current.include_prorated_deprovisions =  mr.x_175353_contracts_include_prorated_deprovisions;
					current.include_prorated_provisions =  mr.x_175353_contracts_include_prorated_provisions;
				}
			}
		}
	}
})(current, previous);
