sys_script_include.script:
var ThriveInvoiceUtil = Class.create();

ThriveInvoiceUtil.SetTimeInvoiced = function(currentref) {
	//not using Secure, so ACLs ignored
	var te = new GlideRecord('task_time_worked');
	te.addQuery('x_175353_thriveinv_invoiced', false);
	te.addQuery('task.sys_domain', currentref.sys_domain);
	te.query();

	while (te.next()) {
		var wr = new GlideRecord('x_175353_thriveinv_labor_rate_card_overrides');
		wr.addQuery('company', te.task.company);
		wr.query();
if(wr.next()) {
gs.addInfoMessage(gs.getMessage(wr.company));
			te.x_175353_thriveinv_billed_rate = wr.hourly_rate;
			te.update();
		}
	}

	gs.addInfoMessage(gs.getMessage('All non-invoiced Time Worked records have been updated.'));
};
