sys_script_include.script:
var GetFormDefaultsService = Class.create();

GetFormDefaultsService.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
   getOverriddenWorkRole:function() {
		var r = new GlideRecord('fm_labor_rate_card');
		r.get(this.getParameter('sysparm_sys_id'));

		var overriddenValues = {};
		overriddenValues.name = r.name.toString();
		overriddenValues.rate_code = r.rate_code.toString();
		overriddenValues.rate_type = r.rate_type.toString();
		overriddenValues.short_description = r.short_description.toString();
		overriddenValues.hourly_rate = r.hourly_rate.toString();

		return JSON.stringify(overriddenValues);
	},
   getOverriddenWorkType:function() {
		var r = new GlideRecord('x_175353_thriveinv_work_types');
		r.get(this.getParameter('sysparm_sys_id'));

		var overriddenValues = {};
		overriddenValues.name = r.name.toString();
		overriddenValues.price_adjustment_type = r.price_adjustment_type.toString();
		overriddenValues.price_adjustment = r.price_adjustment.toString();
		overriddenValues.minimum_hours = r.minimum_hours.toString();
		overriddenValues.maximum_hours = r.maximum_hours.toString();
		overriddenValues.round_to_minutes = r.round_to_minutes.toString();
		overriddenValues.accrual_type = r.accrual_type.toString();
//		overriddenValues.default = r.default.toString();
		overriddenValues.for_utilization = r.for_utilization.toString();

		return JSON.stringify(overriddenValues);
	},
	_privateFunction: function() { // this function is not client callable     
	}
 });
